func TestToDigit(t *testing.T) {
	v := toDigit("aa", 1)
	t.Log(v)
}

func TestDecodeHex(t *testing.T) {
	data, _ := decodeHex("abcd")
	t.Log(data)
}

func TestAesEncryptECB(t *testing.T) {
	origData := []byte("{\"cipherBytes\":\"\",\"publicKey\":\"oT4NexVBTGSrcACkGjzb8kiHuhjRK5kITwHALBHyFWbwsVr4PA976OcH8gmoBtHMs6EuGBwrMTKC\\\\niKeUO9MLSGKf\",\"sign\":\"a90f15f01f464e701f2d938278f774de\"}")
	key := []byte("oT4NexVBTGSrcACk")
	ret := AesEncryptECB(origData, key)
	str := hex.EncodeToString(ret)
	t.Log(str)
}

func TestAesDecryptECB(t *testing.T) {
	origData := ""
	key := []byte("oT4NexVBTGSrcACk")
	ret := AesDecryptECB(origData, key)
	t.Log(string(ret))
}
